import React from 'react';
import apiList from "../lib/apiList";
import { SetPopupContext } from "../App";
import axios from "axios";
import { useContext, useEffect, useState } from "react";
import {
  Button,
  Grid,
  Typography,
  Paper,
  makeStyles,
  TextField,
} from "@material-ui/core";
import ChipInput from "material-ui-chip-input";

const useStyles = makeStyles((theme) => ({
    body: {
      height: "inherit",
    },
    popupDialog: {
      height: "100%",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
  }));

const NewProfile = (props) => {
    const classes = useStyles();
    const setPopup = useContext(SetPopupContext);
    const [userData, setUserData] = useState();
    const [open, setOpen] = useState(false);
  
    const [profileDetails, setProfileDetails] = useState({
      name: "",
      education: [],
      skills: [],
      resume: "",
      profile: "",
    });
  
    const [education, setEducation] = useState([
      {
        institutionName: "",
        startYear: "",
        endYear: "",
      },
    ]);
  
    const handleInput = (key, value) => {
      setProfileDetails({
        ...profileDetails,
        [key]: value,
      });
    };
  
    useEffect(() => {
      getData();
    }, []);
  
    const getData = () => {
      axios
        .get(apiList.user, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        })
        .then((response) => {
          console.log(response.data);
          setProfileDetails(response.data);
          if (response.data.education.length > 0) {
            setEducation(
              response.data.education.map((edu) => ({
                institutionName: edu.institutionName ? edu.institutionName : "",
                startYear: edu.startYear ? edu.startYear : "",
                endYear: edu.endYear ? edu.endYear : "",
              }))
            );
          }
        })
        .catch((err) => {
          console.log(err.response.data);
          setPopup({
            open: true,
            severity: "error",
            message: "Error",
          });
        });
    };
  
    const handleClose = () => {
      setOpen(false);
    };
  
    const editDetails = () => {
      setOpen(true);
    };
  
    const handleUpdate = () => {
      console.log(education);
  
      let updatedDetails = {
        ...profileDetails,
        education: education
          .filter((obj) => obj.institutionName.trim() !== "")
          .map((obj) => {
            if (obj["endYear"] === "") {
              delete obj["endYear"];
            }
            return obj;
          }),
      };
  
      axios
        .put(apiList.user, updatedDetails, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        })
        .then((response) => {
          setPopup({
            open: true,
            severity: "success",
            message: response.data.message,
          });
          getData();
        })
        .catch((err) => {
          setPopup({
            open: true,
            severity: "error",
            message: err.response.data.message,
          });
          console.log(err.response);
        });
      setOpen(false);
    };
  
    return (
      <>
        <Grid
          container
          item
          direction="column"
          alignItems="center"
          style={{ padding: "30px", minHeight: "93vh" }}
        >
          <Grid item>
            <Typography variant="h2">Profile</Typography>
          </Grid>
          <Grid item xs>
            <Paper
              style={{
                padding: "20px",
                outline: "none",
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                alignItems: "center",
              }}
            >
              <Grid container direction="column" alignItems="stretch" spacing={3}>
                <Grid item>
                  <TextField
                    label="Name"
                    value={profileDetails.name}
                    onChange={(event) => handleInput("name", event.target.value)}
                    className={classes.inputBox}
                    variant="outlined"
                    fullWidth
                  />
                </Grid>
                <MultifieldInput
                  education={education}
                  setEducation={setEducation}
                />
                <Grid item>
                  <ChipInput
                    className={classes.inputBox}
                    label="Skills"
                    variant="outlined"
                    value={profileDetails.skills}
                    onAdd={(chip) =>
                      setProfileDetails({
                        ...profileDetails,
                        skills: [...profileDetails.skills, chip],
                      })
                    }
                    onDelete={(chip, index) => {
                      let skills = profileDetails.skills;
                      skills.splice(index, 1);
                      setProfileDetails({
                        ...profileDetails,
                        skills: skills,
                      });
                    }}
                    fullWidth
                  />
                </Grid>
                <Grid item>
                <TextField name="title" label="Resume Link" variant="outlined"  fullWidth value={profileDetails.resume} onChange={(e) => setProfileDetails({ ...profileDetails, resume : e.target.value })} required />
                </Grid>
                <Grid item>
                <TextField name="title" label="Profile Image Link" variant="outlined"  fullWidth value={profileDetails.profile} onChange={(e) => setProfileDetails({ ...profileDetails, profile : e.target.value })} required /> 
                </Grid>
              </Grid>
              <Button
                variant="contained"
                color="primary"
                style={{ padding: "10px 50px", marginTop: "30px" }}
                onClick={() => handleUpdate()}
              >
                Update Details
              </Button>
            </Paper>
          </Grid>
        </Grid>
      </>
    );
  };

export default NewProfile
